L = {
  const L = await require("leaflet/dist/leaflet.js");
  if (!L._style) {
    const href = await require.resolve("leaflet/dist/leaflet.css");
    document.head.appendChild(L._style = html`&lt;link href=${href} rel=stylesheet&gt;`);
  }

  return L;
};







